  AA$pelkm1 = factor(AA$pelkm1, levels = c(-3:-1, 1:13), labels = c("Refused", "Don't Know", NA, "Contacted employer directly/Interview", 
                                                                    "Contacted public employment agency", "Contacted private employment agency", 
                                                                    "Contacted friends or relatives", "Contacted school/university empl center", 
                                                                    "Sent out resumes/filled out application", "Checked union/professional registers", 
                                                                    "Placed or answered ads", "Other active", "Looked at ads", 
                                                                    "Attended job training programs/courses", "Nothing", "Other passive"))
  
  AA$pulkm2 = factor(AA$pulkm2, levels = c(-3:-1, 1:11, 13), labels = c("Refused", "Don't Know", NA, "Contacted employer directly/Interview", 
