  AA$pxcohab = factor(AA$pxcohab, levels = c(-1:3, 10:13, 20:23, 30:33, 40:43, 50, 52:53), 
                      labels = c(NA, "Value: No Change", "Blank: No Change", "Don't Know: No Change", "Refused: No Change", "Value to Value", "Blank to Value", 
